class Validation{isEmail(i){return/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(i)}isString(i){return"string"==typeof i}isEmpty(i){return""==i||null==i}validateSubscription(i){return this.isEmpty(i)?"empty field":!!this.isEmail(i)||"invalid email"}validateMessage(i,t,s){return this.isEmpty(i)||this.isEmpty(t)||this.isEmpty(s)?"empty field":this.isString(i)?this.isEmail(t)?!!this.isString(s)||"invalid message":"invalid email":"invalid name"}}